mapper.select:
SELECT COD.CODE , /* 코드 */ COD.UPPER_CODE , /* 상위_코드 */ CASE WHEN CLA.CODE_NM IS NULL THEN COD.CODE_NM ELSE CLA.CODE_NM END AS CODE_NM , /* 코드_명 */
COD.CODE_DC , /* 코드_설명 */ COD.ADIT_VALUE_ONE , /* 추가_값_1 */ COD.ADIT_VALUE_TWO , /* 추가_값_2 */ COD.ADIT_VALUE_THREE , /* 추가_값_3 */ COD.USE_AT , /* 사용_여부 */ COD.DELETE_AT , /* 삭제_여부 */ COD.EXPSR_ORDR , /* 노출_순서 */ COD.FRST_REGIST_DT , /* 최초_등록_일시 */ COD.FRST_REGISTER_ID , /* 최초_등록자_아이디 */ COD.LAST_UPDT_DT , /* 마지막_수정_일시 */ COD.LAST_UPDUSR_ID /* 마지막_수정자_아이디 */ FROM CO_CODE COD LEFT OUTER JOIN CO_CODE_LANG CLA ON COD.CODE = CLA.CODE AND CLA.LANG_CODE = #{langCode} WHERE COD.DELETE_AT = 'N' AND COD.CODE = #{code}
AND COD.UPPER_CODE = #{upperCode}
ORDER BY COD.EXPSR_ORDR ASC

SELECT COD.CODE , /* 코드 */ COD.UPPER_CODE , /* 상위_코드 */ CASE WHEN CLA.CODE_NM IS NULL THEN COD.CODE_NM ELSE CLA.CODE_NM END AS CODE_NM , /* 코드_명 */
COD.CODE_DC , /* 코드_설명 */ COD.ADIT_VALUE_ONE , /* 추가_값_1 */ COD.ADIT_VALUE_TWO , /* 추가_값_2 */ COD.ADIT_VALUE_THREE , /* 추가_값_3 */ COD.USE_AT , /* 사용_여부 */ COD.DELETE_AT , /* 삭제_여부 */ COD.EXPSR_ORDR , /* 노출_순서 */ COD.FRST_REGIST_DT , /* 최초_등록_일시 */ COD.FRST_REGISTER_ID , /* 최초_등록자_아이디 */ COD.LAST_UPDT_DT , /* 마지막_수정_일시 */ COD.LAST_UPDUSR_ID /* 마지막_수정자_아이디 */ FROM CO_CODE COD LEFT OUTER JOIN CO_CODE_LANG CLA ON COD.CODE = CLA.CODE AND CLA.LANG_CODE = #{langCode} WHERE COD.DELETE_AT = 'N'
AND COD.UPPER_CODE = #{upperCode}
AND COD.USE_AT = #{useAt}
ORDER BY COD.EXPSR_ORDR ASC

mapper.insert:
	INSERT INTO CO_CODE ( 
		CODE,UPPER_CODE,CODE_NM,CODE_DC,ADIT_VALUE_ONE,ADIT_VALUE_TWO,ADIT_VALUE_THREE,USE_AT,DELETE_AT,EXPSR_ORDR,FRST_REGIST_DT,FRST_REGISTER_ID, LAST_UPDT_DT, LAST_UPDUSR_ID 
	) VALUES ( 
		#{code}, #{upperCode}, #{codeNm}, #{codeDc}, #{aditValueOne}, #{aditValueTwo}, #{aditValueThree},#{useAt},#{deleteAt},
		( SELECT MAX(A.EXPSR_ORDR) + 1 FROM CO_CODE A)
		,now(),#{mngrId},now(),#{mngrId}   
	)
	ON DUPLICATE KEY UPDATE
			  CODE_NM 		 	= #{codeNm}
            , ADIT_VALUE_ONE 	= #{aditValueOne}
			, ADIT_VALUE_TWO 	= #{aditValueTwo}
			, ADIT_VALUE_THREE 	= #{aditValueThree}
			, CODE_DC 			= #{codeDc}
			, USE_AT 			= #{useAt}
			, DELETE_AT 		= #{deleteAt}
			, LAST_UPDT_DT 		= now()
			, LAST_UPDUSR_ID	= #{mngrId}

mapper.update:
        INSERT INTO CO_CODE_LANG ( LANG_CODE,CODE,CODE_NM ) VALUES ( #{langCode}, #{code}, #{codeNm} )
	    ON DUPLICATE KEY UPDATE
	           CODE_NM = #{codeNm}

UPDATE CO_CODE
SET DELETE_AT = 'Y'
    , LAST_UPDT_DT = now()
    , LAST_UPDUSR_ID = #{mngrId}
WHERE
    UPPER_CODE = #{upperCode}

UPDATE CO_CODE
    SET UPPER_CODE = #{upperCode}
        , EXPSR_ORDR = ${expsrOrdr}
WHERE
    CODE = #{code}
